/*
import React from "react";
import {NavLink} from "react-router-dom";
import useForm from "./useForm";
import validate from "./validateInfo";

function Signup(){

    const {handleChange, values, handleSubmit} = useForm(validate);


    return (
        <div>
        <h1 style={{color: "white"}}>SIGN UP</h1>
        <img src="/images/Cognixia.png"/>

        <form onSubmit={handleSubmit}>
            <label for="email"></label>
            <input type="text" id="firstName" name="firstName" placeholder="First Name" value={values.firstName} onChange={handleChange} autocomplete="off"></input><br/>
            <label for="email"></label>
            <input type="text" id="lastName" name="lastName" placeholder="Last Name" value={values.lastName} onChange={handleChange} autocomplete="off"></input><br/>
            <label for="email"></label>
            <input type="email" id="email" name="email" placeholder="Email" value={values.email} onChange={handleChange} autocomplete="off"></input><br/>
            <label for="password"></label>
            <input type="password" id="password" name="password" placeholder="Password" value={values.password} onChange={handleChange} autocomplete="off"></input><br/><br/>
            <input type="submit" value="Submit" />
        </form>
        <div style={{paddingTop: "50px"}}>
            <NavLink  style={{ paddingTop: "100px", fontSize: "2.5em", color: "white", textDecoration: "underline", margin: "50px"}} exact to="/" >Home</NavLink>
        </div>
    </div>
    )
}

export default Signup;

